import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import { useProducts } from '../hooks/useProducts';

const Catalog = () => {
  const { products, categories, loading } = useProducts();
  const [searchParams, setSearchParams] = useSearchParams();

  // Состояния из query параметров
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [sortBy, setSortBy] = useState('name');

  // Загрузка параметров из URL при монтировании
  useEffect(() => {
    const categoryParam = searchParams.get('category');
    const minPriceParam = searchParams.get('minPrice');
    const maxPriceParam = searchParams.get('maxPrice');
    const sortParam = searchParams.get('sort');

    if (categoryParam) {
      setSelectedCategories([categoryParam]);
    }
    if (minPriceParam) {
      setPriceRange(prev => ({ ...prev, min: minPriceParam }));
    }
    if (maxPriceParam) {
      setPriceRange(prev => ({ ...prev, max: maxPriceParam }));
    }
    if (sortParam) {
      setSortBy(sortParam);
    }
  }, [searchParams]);

  // Обновление query параметров при изменении фильтров
  useEffect(() => {
    const params = new URLSearchParams();

    if (selectedCategories.length > 0) {
      params.set('category', selectedCategories[0]);
    }
    if (priceRange.min) {
      params.set('minPrice', priceRange.min);
    }
    if (priceRange.max) {
      params.set('maxPrice', priceRange.max);
    }
    if (sortBy !== 'name') {
      params.set('sort', sortBy);
    }

    setSearchParams(params);
  }, [selectedCategories, priceRange, sortBy, setSearchParams]);

  // Фильтрация товаров
  const filteredProducts = products.filter(product => {
    // Фильтр по категориям
    if (selectedCategories.length > 0 && !selectedCategories.includes(product.Категория)) {
      return false;
    }

    // Фильтр по цене
    if (priceRange.min && product.Цена < parseInt(priceRange.min)) {
      return false;
    }
    if (priceRange.max && product.Цена > parseInt(priceRange.max)) {
      return false;
    }

    return true;
  });

  // Сортировка товаров
  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case 'name':
        return a.Наименование.localeCompare(b.Наименование);
      case 'category':
        return a.Категория.localeCompare(b.Категория);
      case 'price-asc':
        return (a.Цена || 0) - (b.Цена || 0);
      case 'price-desc':
        return (b.Цена || 0) - (a.Цена || 0);
      case 'article':
        return a["Артикул продавца"].localeCompare(b["Артикул продавца"]);
      default:
        return 0;
    }
  });

  const handleCategoryChange = (category) => {
    setSelectedCategories(prev =>
      prev.includes(category)
        ? prev.filter(c => c !== category)
        : [...prev, category]
    );
  };

  const handlePriceChange = (field, value) => {
    setPriceRange(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const clearFilters = () => {
    setSelectedCategories([]);
    setPriceRange({ min: '', max: '' });
    setSortBy('name');
    setSearchParams({});
  };

  if (loading) {
    return (
      <div className="catalog">
        <div className="container">
          <div className="catalog-header">
            <h1>Каталог Фонарей</h1>
            <p>Загрузка товаров...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="catalog">
      <div className="container">
        <div className="catalog-header">
          <h1>Каталог Фонарей</h1>
          <p>Более {products.length} моделей профессиональных фонарей</p>
        </div>

        <div className="catalog-content">
          <aside className="catalog-sidebar">
            <div className="filter-header">
              <h3>Фильтры</h3>
              <button className="clear-filters" onClick={clearFilters}>
                Сбросить
              </button>
            </div>

            <div className="filter-group">
              <h4>Категории</h4>
              <div className="checkbox-group">
                {categories.map((category, index) => (
                  <label key={index} className="checkbox-label">
                    <input 
                      type="checkbox" 
                      checked={selectedCategories.includes(category)}
                      onChange={() => handleCategoryChange(category)}
                      className="checkbox-input"
                    />
                    <span className="checkbox-custom"></span>
                    <span className="checkbox-text">{category}</span>
                  </label>
                ))}
              </div>
            </div>

            <div className="filter-group">
              <h4>Цена, ₽</h4>
              <div className="price-filters">
                <div className="price-input-group">
                  <input
                    type="number"
                    placeholder="От"
                    value={priceRange.min}
                    onChange={(e) => handlePriceChange('min', e.target.value)}
                    className="price-input"
                  />
                  <span className="price-separator">—</span>
                  <input
                    type="number"
                    placeholder="До"
                    value={priceRange.max}
                    onChange={(e) => handlePriceChange('max', e.target.value)}
                    className="price-input"
                  />
                </div>
              </div>
            </div>
          </aside>

          <main className="catalog-main">
            <div className="catalog-filters">
              <div className="filter-sort">
                <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                  <option value="name">По наименованию</option>
                  <option value="price-asc">По цене (возрастание)</option>
                  <option value="price-desc">По цене (убывание)</option>
                  <option value="category">По категории</option>
                  <option value="article">По артикулу</option>
                </select>
              </div>
              <div className="filter-view">
                <span>Найдено: {filteredProducts.length} товаров</span>
              </div>
            </div>

            {filteredProducts.length === 0 ? (
              <div className="catalog-placeholder">
                <h3>Товары не найдены</h3>
                <p>Попробуйте изменить параметры фильтра</p>
                <button className="btn-primary" onClick={clearFilters}>
                  Сбросить фильтры
                </button>
              </div>
            ) : (
              <div className="products-grid">
                {sortedProducts.map(product => (
                  <ProductCard key={product["Артикул продавца"]} product={product} />
                ))}
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default Catalog;
