import React from 'react';

const ProductCard = ({ product }) => {
  // Берем первое фото из списка
  const mainImage = product.Фото ? product.Фото.split(';')[0] : '';

  // Форматируем описание (убираем переносы строк)
  const formattedDescription = product.Описание ? 
    product.Описание.split('\n')[0] : '';

  return (
    <div className="product-card">
      <div className="product-image">
        <img src={mainImage} alt={product.Наименование} />
        <span className="product-badge new">New</span>
      </div>
      <div className="product-info">
        <h3 className="product-name">{product.Наименование}</h3>
        <p className="product-category">{product.Категория}</p>

        {/* Характеристики товара */}
        <div className="product-specs">
          <div className="spec">
            <span className="spec-label">Артикул:</span>
            <span className="spec-value">{product["Артикул продавца"]}</span>
          </div>
          {product["Вес с упаковкой (кг)"] && (
            <div className="spec">
              <span className="spec-label">Вес:</span>
              <span className="spec-value">{product["Вес с упаковкой (кг)"]} кг</span>
            </div>
          )}
          {product.Размеры && (
            <div className="spec">
              <span className="spec-label">Размеры:</span>
              <span className="spec-value">
                {product.Размеры.Высота}×{product.Размеры.Длина}×{product.Размеры.Ширина} см
              </span>
            </div>
          )}
        </div>

        <p className="product-description">{formattedDescription}</p>

        {/* Убрана кнопка "В корзину" */}
      </div>
    </div>
  );
};

export default ProductCard;
