import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';
import { useProducts } from '../hooks/useProducts';

const Catalog = () => {
  const { products, categories, loading } = useProducts();
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [sortBy, setSortBy] = useState('name');

  // Фильтрация товаров
  const filteredProducts = products.filter(product => {
    if (selectedCategories.length === 0) return true;
    return selectedCategories.includes(product.Категория);
  });

  // Сортировка товаров
  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case 'name':
        return a.Наименование.localeCompare(b.Наименование);
      case 'category':
        return a.Категория.localeCompare(b.Категория);
      case 'price':
        return (a.Цена || 0) - (b.Цена || 0);
      case 'article':
        return a["Артикул продавца"].localeCompare(b["Артикул продавца"]);
      default:
        return 0;
    }
  });

  const handleCategoryChange = (category) => {
    setSelectedCategories(prev =>
      prev.includes(category)
        ? prev.filter(c => c !== category)
        : [...prev, category]
    );
  };

  if (loading) {
    return (
      <div className="catalog">
        <div className="container">
          <div className="catalog-header">
            <h1>Каталог Фонарей</h1>
            <p>Загрузка товаров...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="catalog">
      <div className="container">
        <div className="catalog-header">
          <h1>Каталог Фонарей</h1>
          <p>Более {products.length} моделей профессиональных фонарей</p>
        </div>

        <div className="catalog-content">
          <aside className="catalog-sidebar">
            <div className="filter-group">
              <h3>Категории</h3>
              <ul className="categories-list">
                {categories.map((category, index) => (
                  <li key={index}>
                    <label>
                      <input 
                        type="checkbox" 
                        checked={selectedCategories.includes(category)}
                        onChange={() => handleCategoryChange(category)}
                      />
                      {category}
                    </label>
                  </li>
                ))}
              </ul>
            </div>

            <div className="filter-group">
              <h3>Всего товаров</h3>
              <p>{filteredProducts.length} из {products.length}</p>
            </div>
          </aside>

          <main className="catalog-main">
            <div className="catalog-filters">
              <div className="filter-sort">
                <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                  <option value="name">Сортировка: По наименованию</option>
                  <option value="category">По категории</option>
                  <option value="price">По цене (возрастание)</option>
                  <option value="article">По артикулу</option>
                </select>
              </div>
              <div className="filter-view">
                <span>Показано: {filteredProducts.length} из {products.length} товаров</span>
              </div>
            </div>

            {filteredProducts.length === 0 ? (
              <div className="catalog-placeholder">
                <h3>Товары не найдены</h3>
                <p>Попробуйте изменить параметры фильтра</p>
              </div>
            ) : (
              <div className="products-grid">
                {sortedProducts.map(product => (
                  <ProductCard key={product["Артикул продавца"]} product={product} />
                ))}
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default Catalog;
